import React, { useState } from 'react'
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';

const Cropper = () => {
    const [src, selectFile] = useState(null)
    const handleFileChange = e => {
        selectFile(URL.createObjectURL(e.target.files[0]))
    }

    const [image, setImage] = useState(null)
    const [crop, setCrop] = useState(null);
    function cropImageFunc() {
        document.getElementById('row').innerHTML = 'This is new.'
    }
    return (
        <div className='container'>
            <div className='row'>
                <div className='col-6'>
                    <input type='file' accept='image/*' onChange={handleFileChange} />
                </div>

               
                {src && <div className='col-6'>
                    <ReactCrop src={src} onImageLoaded={setImage} crop={crop} onChange={newCrop => setCrop(newCrop)} />
                    <button className='cropImage' onClick={cropImageFunc}>Crop Image</button>
                </div>}
            </div>
        </div>
    )
}

export default Cropper
