import { json } from 'express';
import React, {Component} from 'react'
import { JsonToTable } from "react-json-to-table";

class DisplayTable extends Component {
    constructor(props) {
        super(props)
        this.state = null;
        this.getData()
    }

    makeTimer(){
        setInterval(() => {
            var retrievedData = localStorage.getItem('sectionInfo')
            this.setState(JSON.parse(retrievedData))
        }, 1000)
    }

    render() {
        if (this.state === null) {
            return (
                <div>
                    <h1>
                        Test
                    </h1>
                </div>
            )
        } else {
            return (  
                <div className="displayTable">
                    <JsonToTable json={this.state} />
                </div>
            )
        }
        
    }
}
    
export default DisplayTable
