import React, { useState } from 'react'
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';



function App() {
  const [src, selectFile] = useState(null)
  const handleFileChange = e => {
      selectFile(URL.createObjectURL(e.target.files[0]))
  }

  const [image, setImage] = useState(null)
  const [crop, setCrop] = useState(null);

  function cropImageFunc(table) {
    var tableRef = document.getElementById(table);
    var newRow   = tableRef.insertRow(-1);

    var newCell  = newRow.insertCell(0);
    var newElem = document.createElement( 'input' );
    newCell.innerHTML = crop.x;
    //newElem.setAttribute("scope", "row");
    //newCell.appendChild(newElem);

    newCell  = newRow.insertCell(1);
    newElem = document.createElement( 'input' );
    newCell.innerHTML = crop.y;
    //newElem.setAttribute("scope", "row");
    //newCell.appendChild(newElem);

    newCell  = newRow.insertCell(2);
    newElem = document.createElement( 'input' );
    newCell.innerHTML = crop.x + crop.width;
    //newElem.setAttribute("scope", "row");
    //newCell.appendChild(newElem);

    newCell  = newRow.insertCell(3);
    newElem = document.createElement( 'input' );
    newCell.innerHTML = crop.y + crop.height;
    //newElem.setAttribute("scope", "row");
    //newCell.appendChild(newElem);

    newCell  = newRow.insertCell(4);
    newElem = document.createElement( 'input' );
    newCell.innerHTML = 0;
    //newElem.setAttribute("scope", "row");
    //newCell.appendChild(newElem);

    console.log("Hi!")
  }

  function updatePOS(table) {
    var tableRef = document.getElementById(table);

    table.rows[0].cells[0] = crop.x
    table.rows[0].cells[1] = crop.y
    table.rows[0].cells[2] = crop.x + crop.width
    table.rows[0].cells[3] = crop.y + crop.height
  }

    return (
        <div className='container'>
            <div className='row'>
                <div className='col-6'>
                    <input type='file' accept='image/*' onChange={handleFileChange} />
                </div>


                {src && <div className='col-6'>
                    <ReactCrop src={src} onImageLoaded={setImage} crop={crop} onChange={setCrop} />
                </div>}
            </div>

            <button className='crop_button' onClick={() => {cropImageFunc('position_table')}}>Crop Image</button>
            updatePOS(current_positions)
            <table id="current_positions">
              <tr>
                <td>x_min</td>
                <td>y_min</td>
                <td>x_max</td>
                <td>y_min</td>
                <td>class_id</td>
              </tr>
            </table>

            <table id="position_table">
              <tr>
                <td>x_min</td>
                <td>y_min</td>
                <td>x_max</td>
                <td>y_min</td>
                <td>class_id</td>
              </tr>
            </table>
        </div>
    )
}

export default App;
