import React, {Component} from 'react'
import { JsonToTable } from "react-json-to-table";

class DisplayTable extends Component {
    
    constructor(props){
        super(props)
        this.state = {Number : 0}
        this.makeTimer()
      }
      
      makeTimer(){
        setInterval(() => {
          var dataInfo = localStorage.getItem('sectionInfo');
          this.state = JSON.parse(dataInfo)
        }, 1000)
      }
      render(){
        return (
          <div>
            <JsonToTable json = {this.state} />
          </div>
        )
      }
}
    
export default DisplayTable
