import React, { useState } from 'react'
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';

function App() {
  const [src, selectFile] = useState(null)
    const handleFileChange = e => {
        selectFile(URL.createObjectURL(e.target.files[0]))
    }

    const [image, setImage] = useState(null)
    const [crop, setCrop] = useState(null);
    function cropImageFunc(table) {
      var tableRef = document.getElementById(table)
    }


    return (
        <div className='container'>
            <div className='row'>
                <div className='col-6'>
                    <input type='file' accept='image/*' onChange={handleFileChange} />
                </div>


                {src && <div className='col-6'>
                    <ReactCrop src={src} onImageLoaded={setImage} crop={crop} onChange={newCrop => setCrop(newCrop)} />
                    <button className='cropImage' onClick={cropImageFunc('position_table')}>Crop Image</button>
                </div>}
            </div>

            <table id="position_table">
              <tr>
                <td><input type="text" name="links" /></td>
                <td><input type="text" name="keywords" /></td> 
                <td><input type="text" name="violationtype" /></td>
                <td><input type="button" value="Delete Row" onclick="SomeDeleteRowFunction(this)"/></td>          
                </tr>
            </table>
            <input type="submit" class="button" value="Add another line" onclick="addField('tbl');" />



        </div>


    )
}

export default App;
