import React, { useState } from 'react'
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';

function cropImageFunc(table) {
  var tableRef = document.getElementById(table);
  var newRow   = tableRef.insertRow(-1);

  var newCell  = newRow.insertCell(0);
  var newElem = document.createElement( 'input' );
  newElem.setAttribute("name", "links");
  newElem.setAttribute("type", "text");
  newCell.appendChild(newElem);
  console.log("Hi!")
}

function App() {
  const [src, selectFile] = useState(null)
  const handleFileChange = e => {
      selectFile(URL.createObjectURL(e.target.files[0]))
  }

  const [image, setImage] = useState(null)
  const [crop, setCrop] = useState(null);



    return (
        <div className='container'>
            <div className='row'>
                <div className='col-6'>
                    <input type='file' accept='image/*' onChange={handleFileChange} />
                </div>


                {src && <div className='col-6'>
                    <ReactCrop src={src} onImageLoaded={setImage} crop={crop} onChange={setCrop} />
                </div>}
            </div>

            <button className='crop_button' onClick={() => {cropImageFunc('position_table')}}>Crop Image</button>

            <table id="position_table">
              <tr>
                <td>x_min</td>
                <td>y_min</td>
                <td>x_max</td>
                <td>y_min</td>
                <td>class_id</td>
              </tr>
              <tr>
                <td><input type="text" name="x_min" /></td>
                <td><input type="text" name="y_min" /></td> 
                <td><input type="text" name="x_max" /></td>
                <td><input type="text" name="y_max" /></td>
                <td><input type="text" name="class_id" /></td>         
              </tr>
            </table>
        </div>
    )
}

export default App;
